package br.com.seasyc.providingaccount.views.fragments;

import android.os.Bundle;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.ProgressBar;

import androidx.annotation.NonNull;
import androidx.fragment.app.Fragment;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import java.util.ArrayList;
import java.util.List;

import br.com.seasyc.providingaccount.R;
import br.com.seasyc.providingaccount.helpers.GenerateKey;
import br.com.seasyc.providingaccount.models.Receipt;
import br.com.seasyc.providingaccount.views.adapters.ReceipterAdapter;

public class ReceiptsFragment extends Fragment {

    private ProgressBar progressBar;
    private RecyclerView recyclerView;

    private LinearLayoutManager linearLayoutManager;
    private ReceipterAdapter receipterAdapter;

    private List<Receipt> receipts;

    public View onCreateView(@NonNull LayoutInflater inflater,
                             ViewGroup container, Bundle savedInstanceState) {
        View root = inflater.inflate(R.layout.fragment_receipts, container, false);

        initComponents(root);
        configLayout();
        loadReceipts();
        return root;
    }

    private void initComponents(View root) {
        progressBar = root.findViewById(R.id.frg_receipts_pb);
        recyclerView = root.findViewById(R.id.frg_receipts_rv);
        receipts = new ArrayList<>();
    }

    private void configLayout() {
        linearLayoutManager = new LinearLayoutManager(getContext());
        recyclerView.setLayoutManager(linearLayoutManager);
        recyclerView.setHasFixedSize(true);
        receipterAdapter = new ReceipterAdapter(getContext(), receipts);
        recyclerView.setAdapter(receipterAdapter);
    }

    private void loadReceipts() {
        progressBar.setVisibility(View.GONE);
        for (int i = 0; i < 13; i++) {
            Receipt receipt = new Receipt();
            receipt.setId(i);
            receipt.setName("Receipt " + i);
            receipt.setDescription("Descriptio teste genereted on loop");
            receipt.setKey(GenerateKey.generate());
            receipt.setDate("yyyy-MM-dd");
            receipt.setPrice("R$" + i + ",00");
            if ((i / 2) == 3) {
                receipt.setImg_url("https://img.freepik.com/vetores-gratis/imagens-animadas-abstratas-neon-lines_23-2148344065.jpg?size=626&ext=jpg");
                receipt.setCategory("Others");
            } else if ((i / 2) == 4) {
                receipt.setImg_url("data:image/jpeg;base64,");
                receipt.setCategory("Food");
            } else if ((i / 4) == 3 || (i / 4) == 2) {
                receipt.setImg_url("data:image/jpeg;base64,");
                receipt.setCategory("Accommodation");
            } else {
                receipt.setImg_url("https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcSXQSzbn_rZgdttyVRIboQTwTFfgFdoNdP7RA&usqp=CAU");
                receipt.setCategory("Transport");
            }
            receipts.add(receipt);
        }
    }

}